    const [form, setForm] = useState({title: '', text: ''})
    const [id, setId] = useState(uniqid())

    function handleInputChange(event) {
        const {value, name} = event.target
        setForm({...form, [name]: value, id})
        //console.log(form)
    }

    function saveNote() {
        if (form.title !== '' || form.text !== ''){
            setNotes(note => [...note, form]);
            setId(uniqid());
            setForm({title:'', text:''});
            db.collection('dbnotes').add(form)
        }
    }

    //console.log(setNotes)
--------------------------------------------------------------------------------*/
console.log(ide)

function addNoteEleToDB(){
    var n=noteele.length+1
      db.collection('boards').doc({id:ide}).get().then(doc =>{
      setNotes(doc.notes)
    })
    noteele.push({
      id:n,
      title:'noteele '+n,
      noteele:[]
    })
    notes.filter(note =>{
      if(note.id===noteId){
        note.noteele.push({
          id:n,
          title:'noteele '+n,
          noteele:[]
        })
      }
    })
    db.collection('boards').doc({id:ide}).update({
      notes:notes
    })
  }


  function deleteNoteEleFromDB(givenId){
    db.collection('boards').doc({id:ide}).get().then(doc =>{
      setNotes(doc.notes)
    })
    var n=noteele.findIndex(a => a.id===givenId)
    noteele.splice(n,1)
    notes.filter(note =>{
      if(note.id===noteId){
        note.noteele.splice(n,1)
    }
  })
    db.collection('boards').doc({id:ide}).update({
      notes:notes
    })
  }


  function handleChange(event){
    const reader = new FileReader();
   
          reader.addEventListener("load", ()  => {
         localStorage.setItem ("recent-image" , reader.result);
          });
          reader.readAsDataURL(event.target.files[0]);
  }


  function filePost(){
    const source=localStorage.getItem("recent-image");
    if(source)// will only execute if user has selected a file
    {
        db.collection('boards').doc({id:ide}).get().then(doc =>{
            setNotes(doc.notes)
          })
          var n=noteele.length+1
          noteele.push({
                id:n,
                type: 'image',
                iurl: source,
                content:null
          })
          notes.filter(note =>{
            if(note.id===noteId){
              note.noteele.push({
                id:n,
                type: 'image',
                iurl: source,
                content:null
              })
            }
          })
          db.collection('boards').doc({id:ide}).update({
            notes:notes
          })
    }
  }


  function textNote(){
    db.collection('boards').doc({id:ide}).get().then(doc =>{
        setNotes(doc.notes)
      })
      var n=noteele.length+1
      noteele.push({
      id:n,
      type:'textNote',
      iurl:null,
      content: ""
    })
    notes.filter(note =>{
        if(note.id===noteId){
          note.noteele.push({
            id:n,
            type:'textNote',
            iurl:null,
            content: ""
          })
        }
      })
      db.collection('boards').doc({id:ide}).update({
        notes:notes
      })
  }
  function changeTitle(event){
    db.collection('boards').doc({id:ide}).get().then(doc =>{

      setNotes(doc.notes)
    })
    var newTitle=event.target.value
    notes.filter(note =>{
      if(note.id===noteId){
        note.title=newTitle
      }
    })
    db.collection('boards').doc({id:ide}).update({
      notes:notes
    })
  }



  function textNoteHandleChange(givenId){
      var tArea=document.getElementById(givenId)
      var cont=tArea.value
      console.log(tArea)
    db.collection('boards').doc({id:ide}).get().then(doc =>{

        setNotes(doc.notes)
      })
      console.log(noteele)
      console.log(givenId)
      var n=noteele.findIndex(a => a.id===givenId)
      console.log(n)
      notes.filter(note =>{
        if(note.id===noteId){
          console.log(note)
          console.log(note)
          console.log(n)
          note.noteele[n].content=cont
        }
      })

      db.collection('boards').doc({id:ide}).update({
        notes:notes
      })
  }

