    db.collection('boards').doc({id:ide}).get().then(doc =>{
      if(doc){
      setNotes(doc.notes)
        notes.filter(note =>{
          if(note && note.id===noteId){
            setNoteele(note.noteele)
          }
        })
      }
    })
    }

    function Happy(){
      db.collection('boards').doc({id:ide}).get().then(doc =>{
        setNotes(doc.notes)
      })
      var n=noteele.length+1
      noteele.push({
            id:n,
            type: 'emotions',
            iurl: "data:image/png;base64,",
            content:null
      })
      notes.filter(note =>{
        if(note.id===noteId){
          note.noteele=noteele
        }
        db.collection('boards').doc({id:ide}).update({
          notes:notes
        })
      })
    }


    function Sad(){
      db.collection('boards').doc({id:ide}).get().then(doc =>{
        setNotes(doc.notes)
      })
      var n=noteele.length+1
      noteele.push({
            id:n,
            type: 'emotions',
            iurl:  "data:image/png;base64,",
            content:null
      })
      notes.filter(note =>{
        if(note.id===noteId){
          note.noteele=noteele
        }
        db.collection('boards').doc({id:ide}).update({
          notes:notes
        })
      })
     }


